$(document).ready(function(){

    firstApi();
        
    function firstApi(){
        var queryURL1 = "https://cors-anywhere.herokuapp.com/https://jobs.github.com/positions.json?description=Front%20End&location=united%20states";
        $.get(queryURL1).then(function(response){
            console.log(response);
            for(var i = 0; i<response.length; i++){
                $("#results").append($("<tr>").addClass("collapsible", "col-12").append(($)("<img>").attr("src",response[i].company_logo )).append("   " + response[i].title + " - " + response[i].location).append($("<tr>").addClass("content").append(response[i].description)).append($("<a> Apply </a>").addClass("button").attr("href", response[i].url)));
            }

        })
    }
        
});